import React, { useEffect, useState } from "react";

import { Card, CardHeader, CardBody, Row, Col,Button, Modal, ModalHeader, ModalBody, ModalFooter} from "reactstrap";
// reactstrap components
import {
  Table,
} from "reactstrap";

import { ListVecinoFila } from "./ListVecinoFila";

function ListVecino() {

  const [vecinos, setVecinos] = useState([]);

  // Consulta a la API
  useEffect(() => {
    let request = new Request('http://localhost:4000/api/vecino/getallvecinos', {
      method: 'GET',
      mode: 'cors',
      credentials: 'omit',
      referrerPolicy: 'no-referrer',
      headers: {
        'x-token': localStorage.getItem('token') || ''
      }
    });
    fetch(request)
      .then(response => response.json())
      .then(dataJSON => {
        const { data } = dataJSON;
        console.log(data);
        setVecinos(data);
      })
      .catch(err => {
        console.error(err);
      })
  }, []);

  if (vecinos === undefined) return <h1 className="my-4 text-center bg-blue">CARGANDO VECINOS, POR FAVOR ESPERE...</h1>;


  //Actualizar vecino

  
  return (
    <>
      <div className="content">
        <Row>
          <Col md="12">
            <Card>
              <CardHeader>
                <h5 className="title">Lista Vecinos</h5>
              </CardHeader>
              <CardBody>
                <Table className="tablesorter">
                  <thead className="text-primary">
                    <tr>
                      <th>ID VECINO</th>
                      <th>Dirección</th>
                      <th>Numero Contacto 1</th>
                      <th>Nombre Contacto 1</th>
                      <th>Numero Contacto 2</th>
                      <th>Nombre Contacto 2</th>
                      <th className="text-center">Boton</th>
                    </tr>
                  </thead>
                  <tbody>
                    {
                      vecinos.map(vecino => (
                        <ListVecinoFila
                          key={vecino.id_veci}
                          vecino={vecino}
                        />
                      ))
                    }
                  </tbody>
                </Table>
              </CardBody>
            </Card>
          </Col>
        </Row>
        
        <div>
          <Button color="danger" onClick={toggle}>{buttonLabel}</Button>
          <Modal isOpen={modal} toggle={toggle} className={className}>
          <ModalHeader toggle={toggle}>Modal title</ModalHeader>
          <ModalBody>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
          </ModalBody>
          <ModalFooter>
            <Button color="primary" onClick={toggle}>Do Something</Button>{' '}
            <Button color="secondary" onClick={toggle}>Cancel</Button>
          </ModalFooter>
          </Modal>
        </div>
      </div>
    </>
  );
}

export default ListVecino;
